package com.class3;

public class modularRemainders {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     
		int a=10;
        int b=4;
        int e=-5+4*6;
     System.out.println(a%b);
     System.out.println(e);
     int x=(22+9)%7;
     System.out.println(x);
     int y=20+ -3*5 /8;
     System.out.println(y);
     int z= 5 + 15 / 3 * 2 - 8 % 3;
     System.out.println(z);
     //5*2 10+5 15-2
	}

}
