package com.homwork;

public class modularOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int boys, girls;
    boys=10;
    girls=3;
   int people= boys%girls;
   System.out.println(people);
   ++boys;
   System.out.println(boys);
   int a=6;
   int b=14;
   
   int remainder=b%a;
   
   System.out.println (remainder);
   
	
	}

}
